// Render the pre-rendered tile graphics buffers in priority order
// Lower priority = drawn first (behind), higher priority = drawn last (in front)
function drawOptimizedTiles() {
    if (!tileLayers || tileLayers.length === 0) return;
    
    // Sort layers by priority (ascending - lower numbers drawn first)
    const sortedLayers = [...tileLayers].sort((a, b) => a.priority - b.priority);
    
    push();
    imageMode(CORNER);
    
    // Draw each layer in priority order
    for (const layer of sortedLayers) {
        if (layer.buffer) {
            // Draw the cached tile graphics at the exact tile origin position
            // The buffer already contains the tiles at the correct relative positions
            image(layer.buffer, layer.startX, layer.startY, layer.buffer.width, layer.buffer.height);
        }
    }
    
    pop();
}

// Cleanup function for tile system
function cleanupTileSystem() {
    bricksBuilt = false;
    
    // Remove all layer graphics buffers
    if (tileLayers && tileLayers.length > 0) {
        for (const layer of tileLayers) {
            if (layer.buffer && layer.buffer.remove) {
                layer.buffer.remove();
            }
        }
        tileLayers = [];
    }
    
    // Remove simplified collision sprites
    if (simplifiedTileColliders && simplifiedTileColliders.length > 0) {
        for (const collider of simplifiedTileColliders) {
            if (collider && collider.remove) {
                collider.remove();
            }
        }
        simplifiedTileColliders = [];
    }
}


function drawCloud(x, y, s = 1) {
    push();
    noStroke();

    // back layer
    fill(255, 255, 255, 80);
    ellipse(x, y, 140 * s, 80 * s);
    ellipse(x - 40 * s, y + 8 * s, 90 * s, 60 * s);
    ellipse(x + 45 * s, y + 5 * s, 100 * s, 65 * s);

    // middle layer
    fill(255, 255, 255, 140);
    ellipse(x - 25 * s, y - 10 * s, 70 * s, 55 * s);
    ellipse(x + 20 * s, y - 14 * s, 74 * s, 58 * s);
    ellipse(x + 60 * s, y + 2 * s, 62 * s, 48 * s);

    // front puffs
    fill(255, 255, 255, 210);
    ellipse(x - 5 * s, y - 18 * s, 56 * s, 46 * s);
    ellipse(x + 28 * s, y - 16 * s, 52 * s, 42 * s);

    // base shading + second pass (richer bottom)
    fill(220, 228, 240, 120);
    ellipse(x + 5 * s, y + 18 * s, 120 * s, 38 * s);

    fill(205, 214, 230, 110);
    ellipse(x + 10 * s, y + 20 * s, 100 * s, 28 * s);

    fill(190, 200, 220, 80);
    ellipse(x + 18 * s, y + 22 * s, 78 * s, 22 * s);

    pop();
}
