function drawPositionMenu() {
    camera.off();
    push();

    const formatPosition = (x, y) => {
        if (typeof x !== 'number' || typeof y !== 'number') return 'N/A';
        return `(${Math.round(x)}, ${Math.round(y)})`;
    };

    const lines = [];
    lines.push('Position Menu');
    lines.push(`Ball: ${ball ? formatPosition(ball.x, ball.y) : 'N/A'}`);

    levels.forEach((level, index) => {
        lines.push(`${index + 1}. ${level.name}`);

        const checkpoints = Array.isArray(level.checkpoints) ? level.checkpoints : [];
        if (checkpoints.length === 0) {
            lines.push('  Checkpoints: None');
        } else {
            checkpoints.forEach((checkpoint, cpIndex) => {
                const label = checkpoints.length > 1 ? `  Checkpoint ${cpIndex + 1}:` : '  Checkpoint:';
                lines.push(`${label} ${formatPosition(checkpoint.x, checkpoint.y)}`);
            });
        }

        const goal = level.goalPosition ? formatPosition(level.goalPosition.x, level.goalPosition.y) : 'N/A';
        lines.push(`  Goal: ${goal}`);
    });

    const padding = 12;
    const lineHeight = 16;
    const panelWidth = Math.min(340, width - 40);
    const panelHeight = padding * 2 + lines.length * lineHeight;
    const panelX = width - panelWidth - 20;
    const panelY = 20;

    rectMode(CORNER);
    noStroke();
    fill(0, 180);
    rect(panelX, panelY, panelWidth, panelHeight, 8);

    textAlign(LEFT, TOP);
    textSize(12);
    fill(255);

    let textY = panelY + padding;
    lines.forEach(line => {
        text(line, panelX + padding, textY);
        textY += lineHeight;
    });

    pop();
    camera.on();
}

function drawSigns() {
    const level = levels[currentLevel];
    if (!level.signs || level.signs.length === 0) return;

    push();
    textAlign(CENTER, CENTER);
    noStroke();

    for (let sign of level.signs) {
        textSize(sign.size || 24);
        fill(sign.color || 'white');
        text(sign.text, sign.x, sign.y);
    }

    pop();
}

// lol this makes the confetti its alot of spam of just tiles that fall
class Confetto {
    constructor(x, y) {
        this.x = x;
        this.y = y;
        this.vx = random(-5, 5);
        this.vy = random(-15, -5);
        this.gravity = 0.5;
        this.size = random(8, 15);
        this.rotation = random(TWO_PI);
        this.rotationSpeed = random(-0.2, 0.2);
        this.color = random([
            color(255, 0, 0),     // red
            color(255, 165, 0),   // orange
            color(255, 255, 0),   // yellow
            color(0, 255, 0),     // green
            color(0, 0, 255),     // blue
            color(128, 0, 255),   // purple
            color(255, 192, 203)  // pink
        ]);
        this.alpha = 255;
        this.lifespan = 120; // frames
    }

    update() {
        this.vy += this.gravity;
        this.x += this.vx;
        this.y += this.vy;
        this.rotation += this.rotationSpeed;
        this.lifespan--;
        this.alpha = map(this.lifespan, 0, 120, 0, 255);
    }

    display() {
        push();
        translate(this.x, this.y);
        rotate(this.rotation);
        fill(red(this.color), green(this.color), blue(this.color), this.alpha);
        noStroke();
        rectMode(CENTER);
        rect(0, 0, this.size, this.size);
        pop();
    }
// DEATH TO ALL PARTICLES 
    isDead() {
        return this.lifespan <= 0;
    }
}

// spawn confetti
function spawnConfetti(x, y, count = 100) {
    confetti = [];
    for (let i = 0; i < count; i++) {
        confetti.push(new Confetto(x, y));
    }
    confettiActive = true;
}

// update and draw confetti
function updateAndDrawConfetti() {
    if (!confettiActive) return;

    // Uraw each confetto piexe
    for (let i = confetti.length - 1; i >= 0; i--) {
        confetti[i].update();
        confetti[i].display();
        
        if (confetti[i].isDead()) {
            confetti.splice(i, 1);
        }
    }

    // Once all confetti is DEAD it ends the functon 
    if (confetti.length === 0) {
        confettiActive = false;
    }
}

